import React from 'react'
import { Link, useNavigate } from "react-router-dom";
import axios from 'axios'
import {useState} from 'react'
import "../App.css"

const Login = () => {
    const [inputs,setInputs] = useState({
        username:"",
        password:""
      })
    
      //to catch registering a username already made
      const [err,setError] = useState(null)
    
      //function to navigate to login after registering
      const navigate = useNavigate()
    
      //async function because we are making an api request
      const handleSubmit = async e =>{
        e.preventDefault()
        try{
          await axios.post("/authen/register", inputs)
          navigate("/login")
        }catch(err){
          setError(err.response.data); //path of the error message in inpsect
        }
      }
    
      const handleChange = e => {
        setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
      }


  return (
    <div className='userSys'>
        <h1>Login</h1>
        <form>
            <input requried type="text" placeholder='username' name = "username" onChange = {handleChange}/>
            <input requried type="password" placeholder='password' name = "password" onChange = {handleChange}/>
            <button onClick = {handleSubmit}>Login</button>
            {err && <p>{err}</p>}
            <span>Don't have an account? <Link to="/register">Register</Link></span>
        </form>
    </div>
  )
}

export default Login
