import React, { useState } from 'react';
import { Modal, ModalHeader, ModalBody, ModalFooter, Button } from "react";
import { Navigate } from 'react-router-dom';

const ModalComponent = (props) => {
  const {
    buttonText,
    title,
    actionButtonText,
    cancelButtonText,
    children,
    className
  } = props;

  const [modal, setModal] = useState(false);

  const toggle = () => setModal(!modal);

  const closeBtn = (
    <button className='close' onClick={toggle}>
      &times;
    </button>
  );

  return (
    <div>
      <div onClick={toggle}>{buttonText}</div>
      <Modal isOpen={Modal} toggle={toggle} className={className}>
        <ModalHeader className=' Modal' toggle={toggle} close={closeBtn}>{title}
        </ModalHeader>
        <ModalBody className='Modal-Body '>
          <p className='modal-label'>This day is considered a high traffic day. The tables might be limited due to the amount of people</p>
          {children}
        </ModalBody>
        <ModalFooter className='modal-footer border-0'>
          <Button className="btn_secondary modal-btn-Button" onClick={toggle}>
            {cancelButtonText}
          </Button>{" "}
          &nbsp;&nbsp;
          <input
            className='btn btn_primary modal-btn'
            value={actionButtonText}
          />
        </ModalFooter>
      </Modal>
    </div>
  );
};
const SubmitMessage = ({ setIsOpen }) =>  {
  return (
    <>
      <div className='SubmitMessage'>
        <ModalComponent
          title="High Traffic Days"
          cancelButtonText="Cancel"
          actionButtonText="Ok"
          buttonText="Test Button"
        />
      </div>
    </>
  );
}

export default SubmitMessage
